use=\"aui-carousel\">\n		var carousel = new A.Carousel(\n			{\n				after: {\n					responsive: function(event) {\n						event.stopImmediatePropagation();\n\n						var boundingBox = event.currentTarget.get(\'boundingBox\');\n\n						boundingBox.all(\'.image-viewer-base-image-list, .image-viewer-base-image\').setStyles(\n							{\n								height: \'auto\',\n								maxHeight: event.height,\n								maxWidth: event.width,\n								width: \'auto\'\n							}\n						);\n					}\n				},\n\n				contentBox: \'#<@portlet.namespace />carousel\',\n				height: 250,\n				intervalTime: 2,\n				width: 700\n			}\n		).render();\n	</@liferay_aui.script>\n</#if>',0,20102,NULL,'2021-10-03 20:34:06.419000'),(0,0,36739,20123,20097,20102,NULL,'2021-10-03 20:34:06.448000',24117,0,36737,'1.0','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,sv_SE,pt_BR,ja_JP,fr_FR,hu_HU,de_DE,ca_ES,ar_SA,fi_FI,zh_CN,es_ES,nl_NL\" default-locale=\"en_US\"><Name language-id=\"sv_SE\">Sociala ntverk</Name><Name language-id=\"pt_BR\">Social</Name><Name language-id=\"ja_JP\"></Name><Name language-id=\"fr_FR\">Social</Name><Name language-id=\"hu_HU\">Kzssgi</Name><Name language-id=\"de_DE\">Social</Name><Name language-id=\"ca_ES\">Social</Name><Name language-id=\"ar_SA\"></Name><Name language-id=\"fi_FI\">Sosiaalinen</Name><Name language-id=\"en_US\">Social</Name><Name language-id=\"zh_CN\"></Name><Name language-id=\"es_ES\">Social</Name><Name language-id=\"nl_NL\">Sociaal</Name></root>','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,sv_SE,pt_BR,ja_JP,fr_FR,hu_HU,de_DE,ca_ES,ar_SA,fi_FI,zh_CN,es_ES,nl_NL\" default-locale=\"en_US\"><Description language-id=\"sv_SE\">Visar sociala bokmrken och omdmen fr wiki-sidor och deras underordnade sidor.</Description><Description language-id=\"pt_BR\">Exibe marcadores sociais e avaliaes para pginas da wiki e suas pginas filhas.</Description><Description language-id=\"ja_JP\">Wiki</Description><Description language-id=\"fr_FR\