L(printURL.toString()) + \"\'});\"\n	/>\n\n	<@liferay_ui[\"icon\"]\n		icon=\"print\"\n		markupView=\"lexicon\"\n		message=\"print\"\n		url=taglibPrintURL\n	/>\n</#macro>\n\n<#macro getRatings\n	cssClass\n	entry\n>\n	<#if wikiPortletInstanceConfiguration.enablePageRatings()>\n		<div class=\"${cssClass}\">\n			<@liferay_ui[\"ratings\"]\n				className=wikiPageClassName\n				classPK=entry.getResourcePrimKey()\n			/>\n		</div>\n	</#if>\n</#macro>\n\n<#macro getRelatedAssets>\n	<#if assetEntry?? && wikiPortletInstanceConfiguration.enableRelatedAssets()>\n		<@liferay_asset[\"asset-links\"] assetEntryId=assetEntry.getEntryId() />\n	</#if>\n</#macro>',0,20102,NULL,'2021-10-03 20:34:06.448000'),(0,0,37115,20123,20097,20102,NULL,'2021-10-03 20:34:21.202000',27002,37109,37113,'1.0','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,sv_SE,pt_BR,ja_JP,fr_FR,hu_HU,de_DE,ca_ES,ar_SA,fi_FI,zh_CN,es_ES,nl_NL\" default-locale=\"en_US\"><Name language-id=\"sv_SE\">Vanligt innehll</Name><Name language-id=\"pt_BR\">Contedo Web bsico</Name><Name language-id=\"ja_JP\">Web</Name><Name language-id=\"fr_FR\">Contenu web basique</Name><Name language-id=\"hu_HU\">Alapvet webtartalom</Name><Name language-id=\"de_DE\">Einfacher Webcontent</Name><Name language-id=\"ca_ES\">Contigut web bsic</Name><Name language-id=\"ar_SA\"> </Name><Name language-id=\"fi_FI\">Tavallinen web-sislt</Name><Name language-id=\"en_US\">Basic Web Content</Name><Name language-id=\"zh_CN\"> Web </Name><Name language-id=\"es_ES\">Contenido web bsico</Name><Name language-id=\"nl_NL\">Basiswebcontent</Name></root>','','ftl','${content.getData()}',0,20102,NULL,'2021-10-03 20:34:21.202000'),(0,0,37204,20123,20097,20102,NULL,'2021-10-03 20:34:22.445000',28503,0,37202,'1.0','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,sv_SE,pt_BR,ja_JP,fr_FR,hu_HU,de_DE,ca_ES,ar_SA,fi_FI,zh_CN,es_ES,nl_NL\" default-locale=\"en_US\"><Name language-id=\"sv_SE\">Standard</Name><Name language-id=\